contracts/IdentityVerification.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";


/**
 * @title IdentityVerification
 * @dev Identity verification system for UniFi DeFi platform
 */
contract IdentityVerification is Ownable {


    struct Identity {
        uint256 identityId;
        address user;
        string studentId;
        string university;
        string degree;
        uint256 graduationYear;
        bool isVerified;
        bool isActive;
        uint256 verificationDate;
        string ipfsHash;
    }

    struct VerificationRequest {
        uint256 requestId;
        address user;
        string studentId;
        string university;
        string degree;
        uint256 graduationYear;
        string ipfsHash;
        bool isApproved;
        bool isRejected;
        uint256 requestDate;
    }

    // State variables
    uint256 private _identityIds;
    uint256 private _requestIds;

    mapping(uint256 => Identity) public identities;
    mapping(uint256 => VerificationRequest) public verificationRequests;
    mapping(address => uint256) public userIdentityId;
    mapping(address => uint256[]) public userRequests;
    mapping(address => bool) public verifiers;
    mapping(string => bool) public verifiedStudentIds;

    uint256 public constant VERIFICATION_FEE = 10 * 10**6; // 10 USDT
    uint256 public constant VERIFICATION_EXPIRY = 365 days;

    // Events
    event IdentityCreated(uint256 indexed identityId, address indexed user, string studentId);
    event IdentityVerified(uint256 indexed identityId, address indexed user);
    event IdentityDeactivated(uint256 indexed identityId, address indexed user);
    event VerificationRequested(uint256 indexed requestId, address indexed user, string studentId);
    event VerificationApproved(uint256 indexed requestId, address indexed user);
    event VerificationRejected(uint256 indexed requestId, address indexed user, string reason);
    event VerifierAdded(address indexed verifier);
    event VerifierRemoved(address indexed verifier);

    modifier onlyVerifier() {
        require(verifiers[msg.sender] || msg.sender == owner(), "Not authorized verifier");
        _;
    }

    constructor() Ownable(msg.sender) {
        verifiers[msg.sender] = true;
    }

    // Identity Management Functions
    function createIdentity(
        string memory _studentId,
        string memory _university,
        string memory _degree,
        uint256 _graduationYear,
        string memory _ipfsHash
    ) external returns (uint256) {
        require(bytes(_studentId).length > 0, "Student ID required");
        require(bytes(_university).length > 0, "University required");
        require(_graduationYear > 0, "Graduation year required");
        require(userIdentityId[msg.sender] == 0, "Identity already exists");

        _identityIds++;
        uint256 identityId = _identityIds;

        Identity storage identity = identities[identityId];
        identity.identityId = identityId;
        identity.user = msg.sender;
        identity.studentId = _studentId;
        identity.university = _university;
        identity.degree = _degree;
        identity.graduationYear = _graduationYear;
        identity.ipfsHash = _ipfsHash;
        identity.isActive = true;
        identity.verificationDate = block.timestamp;

        userIdentityId[msg.sender] = identityId;

        emit IdentityCreated(identityId, msg.sender, _studentId);
        return identityId;
    }

    function requestVerification(
        string memory _studentId,
        string memory _university,
        string memory _degree,
        uint256 _graduationYear,
        string memory _ipfsHash
    ) external returns (uint256) {
        require(bytes(_studentId).length > 0, "Student ID required");
        require(bytes(_university).length > 0, "University required");
        require(_graduationYear > 0, "Graduation year required");
        require(!verifiedStudentIds[_studentId], "Student ID already verified");

        _requestIds++;
        uint256 requestId = _requestIds;

        VerificationRequest storage request = verificationRequests[requestId];
        request.requestId = requestId;
        request.user = msg.sender;
        request.studentId = _studentId;
        request.university = _university;
        request.degree = _degree;
        request.graduationYear = _graduationYear;
        request.ipfsHash = _ipfsHash;
        request.requestDate = block.timestamp;

        userRequests[msg.sender].push(requestId);

        emit VerificationRequested(requestId, msg.sender, _studentId);
        return requestId;
    }

    function approveVerification(uint256 _requestId) external onlyVerifier {
        VerificationRequest storage request = verificationRequests[_requestId];
        require(request.user != address(0), "Request does not exist");
        require(!request.isApproved && !request.isRejected, "Request already processed");

        request.isApproved = true;

        // Create or update identity
        uint256 identityId = userIdentityId[request.user];
        if (identityId == 0) {
            _identityIds++;
            identityId = _identityIds;
            userIdentityId[request.user] = identityId;
        }

        Identity storage identity = identities[identityId];
        identity.identityId = identityId;
        identity.user = request.user;
        identity.studentId = request.studentId;
        identity.university = request.university;
        identity.degree = request.degree;
        identity.graduationYear = request.graduationYear;
        identity.ipfsHash = request.ipfsHash;
        identity.isVerified = true;
        identity.isActive = true;
        identity.verificationDate = block.timestamp;

        verifiedStudentIds[request.studentId] = true;

        emit VerificationApproved(_requestId, request.user);
        emit IdentityVerified(identityId, request.user);
    }

    function rejectVerification(uint256 _requestId, string memory _reason) external onlyVerifier {
        VerificationRequest storage request = verificationRequests[_requestId];
        require(request.user != address(0), "Request does not exist");
        require(!request.isApproved && !request.isRejected, "Request already processed");

        request.isRejected = true;

        emit VerificationRejected(_requestId, request.user, _reason);
    }

    function deactivateIdentity() external {
        uint256 identityId = userIdentityId[msg.sender];
        require(identityId > 0, "No identity found");

        Identity storage identity = identities[identityId];
        identity.isActive = false;

        emit IdentityDeactivated(identityId, msg.sender);
    }

    function reactivateIdentity() external onlyVerifier {
        uint256 identityId = userIdentityId[msg.sender];
        require(identityId > 0, "No identity found");

        Identity storage identity = identities[identityId];
        identity.isActive = true;
    }

    // Verifier Management Functions
    function addVerifier(address _verifier) external onlyOwner {
        require(_verifier != address(0), "Invalid address");
        require(!verifiers[_verifier], "Already a verifier");

        verifiers[_verifier] = true;

        emit VerifierAdded(_verifier);
    }

    function removeVerifier(address _verifier) external onlyOwner {
        require(verifiers[_verifier], "Not a verifier");

        verifiers[_verifier] = false;

        emit VerifierRemoved(_verifier);
    }

    // View Functions
    function getIdentity(uint256 _identityId) external view returns (
        uint256 identityId,
        address user,
        string memory studentId,
        string memory university,
        string memory degree,
        uint256 graduationYear,
        bool isVerified,
        bool isActive,
        uint256 verificationDate,
        string memory ipfsHash
    ) {
        Identity storage identity = identities[_identityId];
        return (
            identity.identityId,
            identity.user,
            identity.studentId,
            identity.university,
            identity.degree,
            identity.graduationYear,
            identity.isVerified,
            identity.isActive,
            identity.verificationDate,
            identity.ipfsHash
        );
    }

    function getVerificationRequest(uint256 _requestId) external view returns (
        uint256 requestId,
        address user,
        string memory studentId,
        string memory university,
        string memory degree,
        uint256 graduationYear,
        string memory ipfsHash,
        bool isApproved,
        bool isRejected,
        uint256 requestDate
    ) {
        VerificationRequest storage request = verificationRequests[_requestId];
        return (
            request.requestId,
            request.user,
            request.studentId,
            request.university,
            request.degree,
            request.graduationYear,
            request.ipfsHash,
            request.isApproved,
            request.isRejected,
            request.requestDate
        );
    }

    function getUserIdentity(address _user) external view returns (uint256) {
        return userIdentityId[_user];
    }

    function getUserRequests(address _user) external view returns (uint256[] memory) {
        return userRequests[_user];
    }

    function isIdentityVerified(address _user) external view returns (bool) {
        uint256 identityId = userIdentityId[_user];
        if (identityId == 0) return false;
        return identities[identityId].isVerified && identities[identityId].isActive;
    }

    function isStudentIdVerified(string memory _studentId) external view returns (bool) {
        return verifiedStudentIds[_studentId];
    }

    // Worldcoin/Gitcoin Passport Integration
    function verifyWithPassport(
        address _user,
        string memory _studentId,
        string memory _university,
        string memory _degree,
        uint256 _graduationYear,
        string memory _ipfsHash,
        bytes memory _proof
    ) external onlyVerifier {
        // In a real implementation, this would verify the proof against Worldcoin/Gitcoin Passport
        // For now, we'll create the identity directly
        
        uint256 identityId = userIdentityId[_user];
        if (identityId == 0) {
            _identityIds++;
            identityId = _identityIds;
            userIdentityId[_user] = identityId;
        }

        Identity storage identity = identities[identityId];
        identity.identityId = identityId;
        identity.user = _user;
        identity.studentId = _studentId;
        identity.university = _university;
        identity.degree = _degree;
        identity.graduationYear = _graduationYear;
        identity.ipfsHash = _ipfsHash;
        identity.isVerified = true;
        identity.isActive = true;
        identity.verificationDate = block.timestamp;

        verifiedStudentIds[_studentId] = true;

        emit IdentityVerified(identityId, _user);
    }
}

contracts/IPFSStorage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";


/**
 * @title IPFSStorage
 * @dev IPFS metadata storage for UniFi DeFi platform
 */
contract IPFSStorage is Ownable {


    struct IPFSDocument {
        uint256 documentId;
        address owner;
        string ipfsHash;
        string documentType;
        string description;
        uint256 uploadDate;
        bool isVerified;
        bool isActive;
    }

    struct DocumentType {
        string name;
        string description;
        bool isActive;
        uint256 maxSize; // in bytes
    }

    // State variables
    uint256 private _documentIds;

    mapping(uint256 => IPFSDocument) public documents;
    mapping(address => uint256[]) public userDocuments;
    mapping(string => DocumentType) public documentTypes;
    mapping(string => bool) public verifiedHashes;

    string[] public supportedDocumentTypes;

    // Events
    event DocumentUploaded(uint256 indexed documentId, address indexed owner, string ipfsHash, string documentType);
    event DocumentVerified(uint256 indexed documentId, address indexed owner);
    event DocumentDeactivated(uint256 indexed documentId, address indexed owner);
    event DocumentTypeAdded(string indexed documentType, string description);
    event DocumentTypeDeactivated(string indexed documentType);

    constructor() Ownable(msg.sender) {
        // Initialize default document types
        _addDocumentType("student_id", "Student ID Document", 1024 * 1024); // 1MB
        _addDocumentType("university_transcript", "University Transcript", 5 * 1024 * 1024); // 5MB
        _addDocumentType("loan_agreement", "Loan Agreement", 2 * 1024 * 1024); // 2MB
        _addDocumentType("expense_receipt", "Expense Receipt", 1024 * 1024); // 1MB
        _addDocumentType("scholarship_application", "Scholarship Application", 3 * 1024 * 1024); // 3MB
        _addDocumentType("identity_verification", "Identity Verification", 2 * 1024 * 1024); // 2MB
    }

    // Document Management Functions
    function uploadDocument(
        string memory _ipfsHash,
        string memory _documentType,
        string memory _description
    ) external returns (uint256) {
        return _uploadDocument(_ipfsHash, _documentType, _description);
    }

    function _uploadDocument(
        string memory _ipfsHash,
        string memory _documentType,
        string memory _description
    ) internal returns (uint256) {
        require(bytes(_ipfsHash).length > 0, "IPFS hash required");
        require(documentTypes[_documentType].isActive, "Document type not supported");
        require(bytes(_description).length > 0, "Description required");

        _documentIds++;
        uint256 documentId = _documentIds;

        IPFSDocument storage document = documents[documentId];
        document.documentId = documentId;
        document.owner = msg.sender;
        document.ipfsHash = _ipfsHash;
        document.documentType = _documentType;
        document.description = _description;
        document.uploadDate = block.timestamp;
        document.isActive = true;

        userDocuments[msg.sender].push(documentId);

        emit DocumentUploaded(documentId, msg.sender, _ipfsHash, _documentType);
        return documentId;
    }

    function verifyDocument(uint256 _documentId) external onlyOwner {
        IPFSDocument storage document = documents[_documentId];
        require(document.owner != address(0), "Document does not exist");
        require(document.isActive, "Document not active");

        document.isVerified = true;
        verifiedHashes[document.ipfsHash] = true;

        emit DocumentVerified(_documentId, document.owner);
    }

    function deactivateDocument(uint256 _documentId) external {
        IPFSDocument storage document = documents[_documentId];
        require(document.owner == msg.sender, "Not document owner");
        require(document.isActive, "Document already deactivated");

        document.isActive = false;

        emit DocumentDeactivated(_documentId, msg.sender);
    }

    function reactivateDocument(uint256 _documentId) external onlyOwner {
        IPFSDocument storage document = documents[_documentId];
        require(document.owner != address(0), "Document does not exist");
        require(!document.isActive, "Document already active");

        document.isActive = true;
    }

    // Document Type Management Functions
    function addDocumentType(
        string memory _name,
        string memory _description,
        uint256 _maxSize
    ) external onlyOwner {
        require(bytes(_name).length > 0, "Name required");
        require(!documentTypes[_name].isActive, "Document type already exists");

        _addDocumentType(_name, _description, _maxSize);
    }

    function _addDocumentType(
        string memory _name,
        string memory _description,
        uint256 _maxSize
    ) internal {
        DocumentType storage docType = documentTypes[_name];
        docType.name = _name;
        docType.description = _description;
        docType.isActive = true;
        docType.maxSize = _maxSize;

        supportedDocumentTypes.push(_name);

        emit DocumentTypeAdded(_name, _description);
    }

    function deactivateDocumentType(string memory _name) external onlyOwner {
        require(documentTypes[_name].isActive, "Document type not active");

        documentTypes[_name].isActive = false;

        emit DocumentTypeDeactivated(_name);
    }

    // View Functions
    function getDocument(uint256 _documentId) external view returns (
        uint256 documentId,
        address owner,
        string memory ipfsHash,
        string memory documentType,
        string memory description,
        uint256 uploadDate,
        bool isVerified,
        bool isActive
    ) {
        IPFSDocument storage document = documents[_documentId];
        return (
            document.documentId,
            document.owner,
            document.ipfsHash,
            document.documentType,
            document.description,
            document.uploadDate,
            document.isVerified,
            document.isActive
        );
    }

    function getUserDocuments(address _user) external view returns (uint256[] memory) {
        return userDocuments[_user];
    }

    function getDocumentType(string memory _name) external view returns (
        string memory name,
        string memory description,
        bool isActive,
        uint256 maxSize
    ) {
        DocumentType storage docType = documentTypes[_name];
        return (
            docType.name,
            docType.description,
            docType.isActive,
            docType.maxSize
        );
    }

    function getSupportedDocumentTypes() external view returns (string[] memory) {
        return supportedDocumentTypes;
    }

    function isHashVerified(string memory _ipfsHash) external view returns (bool) {
        return verifiedHashes[_ipfsHash];
    }

    function getDocumentCount() external view returns (uint256) {
        return _documentIds;
    }

    // Batch Operations
    function uploadMultipleDocuments(
        string[] memory _ipfsHashes,
        string[] memory _documentTypes,
        string[] memory _descriptions
    ) external returns (uint256[] memory) {
        require(
            _ipfsHashes.length == _documentTypes.length &&
            _documentTypes.length == _descriptions.length,
            "Arrays must have same length"
        );

        uint256[] memory documentIds = new uint256[](_ipfsHashes.length);

        for (uint256 i = 0; i < _ipfsHashes.length; i++) {
            documentIds[i] = _uploadDocument(_ipfsHashes[i], _documentTypes[i], _descriptions[i]);
        }

        return documentIds;
    }

    // Search Functions
    function getDocumentsByType(string memory _documentType) external view returns (uint256[] memory) {
        uint256[] memory tempDocs = new uint256[](_documentIds);
        uint256 count = 0;

        for (uint256 i = 1; i <= _documentIds; i++) {
            if (
                keccak256(bytes(documents[i].documentType)) == keccak256(bytes(_documentType)) &&
                documents[i].isActive
            ) {
                tempDocs[count] = i;
                count++;
            }
        }

        uint256[] memory result = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            result[i] = tempDocs[i];
        }

        return result;
    }

    function getVerifiedDocuments() external view returns (uint256[] memory) {
        uint256[] memory tempDocs = new uint256[](_documentIds);
        uint256 count = 0;

        for (uint256 i = 1; i <= _documentIds; i++) {
            if (documents[i].isVerified && documents[i].isActive) {
                tempDocs[count] = i;
                count++;
            }
        }

        uint256[] memory result = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            result[i] = tempDocs[i];
        }

        return result;
    }
}

